package PatternPrograms;

public class Pattern_Program_17 {

			public static void main(String[] args) {
				// TODO Auto-generated method stub
				
				for (int row=0; row<=4; row++)
				{
					for(int col = 0; col<=row; col++)
					{
						System.out.print(col + " " );
						
					}
					System.out.println();
				}
				

			}

		}




