package BitwiseOperater;
public class Bitwise_Operater {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println( 5 & 3 ); //  Bitwise_OR_Operater
		System.out.println(12 | 9); //  Bitwise_AND_Operater
		System.out.println(~ 9); //  Bitwise_NOT_Operater
		System.out.println(2 ^ 3); //  Bitwise_XOR_Operater
		System.out.println(10 << 2); //  Bitwise_LEFTSHIFT_Operater
		System.out.println(13 << 2); // Bitwise_RIGHTSHIFT_Operater
	} // main close
} // class close
